import pygame
import time
import random

pygame.init()


Screen = pygame.display.set_mode((1280,640))
TileWidth = 40
class Yellow:
    def __init__(self):
        self.x = 2
        self.y = 1
        self.radius = TileWidth/2
        self.Dir = "NULL"
        self.TimePassed = 0
    def DrawPac(self):
        pygame.draw.circle(Screen,(246,255,0),(self.x*TileWidth+self.radius,self.y*TileWidth+self.radius),self.radius-2)

    def LegalMove(self,i,j):
        try:
            if BOARD[j][i].wall == 1:
                return False
            else:
                return True
        except:
            if i > 31:
                return True
            return False
    
    def Move(self,Dir):
        if Dir == "D" and self.LegalMove(self.x+1,self.y) == True:
            self.Dir = Dir
        elif Dir == "S" and self.LegalMove(self.x,self.y+1) == True:
            self.Dir = Dir
        elif Dir == "A" and self.LegalMove(self.x-1,self.y) == True:
            self.Dir = Dir
        elif Dir == "W" and self.LegalMove(self.x,self.y-1) == True:
            self.Dir = Dir

    def Direction(self):
        if self.Loop() == True:
            if self.Dir == "D":
                if self.LegalMove(self.x+1,self.y) == True:
                    self.x += 1
            elif self.Dir == "S":
                if self.LegalMove(self.x,self.y+1) == True:
                    self.y += 1
            elif self.Dir == "A":
                if self.LegalMove(self.x-1,self.y) == True:
                    self.x -= 1
            elif self.Dir == "W":
                if self.LegalMove(self.x,self.y-1) == True:
                    self.y -= 1
            if self.x < 0:
                self.x = 31
            elif self.x > 31:
                self.x = 0
                
    def Loop(self):
        self.TimePassed = self.TimePassed + Counter
        #print(self.TimePassed)
        if self.TimePassed > 0.1:
            self.TimePassed = 0
            return True
        else:
            return False

class Tile:
    def __init__(self,x,y,wall):
        self.x = x
        self.y = y
        self.wall = wall
        self.width = TileWidth
    def DrawTile(self):
        if self.wall == 1:
            pygame.draw.rect(Screen,(24, 31, 181),(self.x*self.width,self.y*self.width,self.width,self.width),1,3,5,5,5,5)
        
class KeyPressed:
    def __init__(self):
        self.button = "NONE"
        self.Direction = "NONE"
        self.PreviousButton = "NONE"
    def Detector(self,ev):
        if ev.type == pygame.KEYDOWN:
            if ev.key == pygame.K_w:
                self.button = "W"
                self.Direction = "UP"
            elif ev.key == pygame.K_s:
                self.button = "S"
                self.Direction = "DOWN"
            elif ev.key == pygame.K_d:
                self.button = "D"
                self.Direction = "RIGHT"
            elif ev.key == pygame.K_a:
                self.button = "A"
                self.Direction = "LEFT"

    


